mapper.select:
SELECT 

FROM work_meeting a

WHERE a.id = #{id}

SELECT 

FROM work_meeting a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.meeting_time = #{meetingTime}


		AND a.theme LIKE 
			'%'||#{theme}||'%'
			'%'+#{theme}+'%'
			concat('%',#{theme},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM work_meeting a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE work_meeting SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
