mapper.select:
SELECT 

FROM work_leave a

WHERE a.id = #{id}

SELECT 

FROM work_leave a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.leave_type = #{leaveType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM work_leave a


	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.state = #{LEAVE_FLAG_DEPT}

		and u.office_id = #{currentUser.office.id}


		AND a.leave_type = #{leaveType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 
	count(*)
FROM work_leave a


	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.state = '0'
	and u.office_id = #{currentUser.office.id}

SELECT 

FROM work_leave a


	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.state = '1'

		AND a.leave_type = #{leaveType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM work_leave a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE work_leave SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
